import { ProjectType, Builder } from 'storybook/internal/cli';
import { PackageManagerName, JsPackageManager } from 'storybook/internal/common';

type CommandOptions = {
    packageManager: PackageManagerName;
    usePnp?: boolean;
    type?: ProjectType;
    force?: any;
    html?: boolean;
    skipInstall?: boolean;
    parser?: string;
    yes?: boolean;
    builder?: Builder;
    linkable?: boolean;
    disableTelemetry?: boolean;
    enableCrashReports?: boolean;
    debug?: boolean;
    dev?: boolean;
};

declare function doInitiate(options: CommandOptions): Promise<{
    shouldRunDev: true;
    projectType: ProjectType;
    packageManager: JsPackageManager;
    storybookCommand: string;
} | {
    shouldRunDev: false;
}>;

export { doInitiate as initiate };
